function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".menu"),s=document.querySelector("body");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked")):(t.classList.add("active"),e.classList.add("active-burger"),s.classList.add("locked"))}));document.querySelectorAll(".menu__item").forEach((i=>{i.addEventListener("click",(function(){t.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(t.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked"))}))}burgerMenu(),document.querySelector(".select-selected").addEventListener("click",(function(){this.nextElementSibling.classList.toggle("show")}));for(var selectOptions=document.querySelectorAll(".select-option"),i=0;i<selectOptions.length;i++)selectOptions[i].addEventListener("click",(function(){var e=this.textContent;document.querySelector(".select-selected").textContent=e,document.querySelector(".select-options").classList.remove("show")}));var firstOption=document.querySelector(".select-option");function winnersSlider(){new Swiper(".winners__slider",{slidesPerView:1,spaceBetween:20,navigation:{nextEl:".winners__arrow-next",prevEl:".winners__arrow-prev"},pagination:{el:".winners__slider-pagination"},breakpoints:{320:{slidesPerView:1,spaceBetween:20},767:{slidesPerView:2,spaceBetween:20},992:{slidesPerView:3,spaceBetween:30}}})}document.querySelector(".select-selected").textContent=firstOption.textContent,winnersSlider();